worksheet.getRange("E7").setValue("Total Monthly Expenses");


//--------------------------------Set Height & Width--------------------------------
worksheet.setStandardHeight(26.25);
worksheet.setStandardWidth(8.43);

worksheet.getRange("2:24").setRowHeight(27);
